"""Favicon Handler module for retrieving and processing website favicons.

This module provides functionality to:
1. Download favicons from websites
2. Convert them to base64-encoded strings
3. Handle various favicon formats and sizes
"""

import base64
import requests
from urllib.parse import urlparse
from logger import Logger

logger = Logger("FaviconHandler")

MANUAL_FAVICONS_OVERRIDES = {
    "github.com": "data:image/png;base64,",
    "youtube.com": "data:image/png;base64,",
    "bing.com": "data:image/png;base64,",
    "chatgpt.co": "data:image/png;base64,",
    "amazon.com": "data:image/png;base64,",
    "pinterest.com": "data:image/png;base64,",
    "autohotkey.com": "data:image/png;base64,"
}

FAVICONS_FOR_GITHUB = {
    "jaba-campaign-api": "data:image/png;base64,",
    "jaba-composite-api": "data:image/png;base64,",
    "jaba-users-api": "data:image/png;base64,",
    "jaba-organizations-api": "data:image/png;base64,",
    "jaba-social-media-api": "data:image/png;base64,"
}

favicon_cache = {}

def get_favicon_string_base64(url: str, size: int = 256) -> str:
    """
    Returns a data:image/png;base64,... string resized to the given size.
    """

    for orgGithub in FAVICONS_FOR_GITHUB:
        if orgGithub in url:
            return FAVICONS_FOR_GITHUB[orgGithub]
    
    # Check cache
    domain = extract_domain(url)
    # Check if manual override exists
    if domain in MANUAL_FAVICONS_OVERRIDES:
        logger.debug(f"Favicon for {domain} in Manual Overrides")
        return MANUAL_FAVICONS_OVERRIDES[domain]
    
    if domain in favicon_cache:
        logger.debug(f"Favicon for {url} in cache")
        return favicon_cache[domain]
    

    try:
        # Remote fetch
        resp = requests.get(f"https://img.logo.dev/{extract_domain(url)}?token=pk_Gb5xYqzMT2KthP8ESZj36g&size=144&format=png&retina=true", timeout=5, headers={"User-Agent": "Mozilla/5.0"})
        resp.raise_for_status()

        # Convert response to base64
        b64 = base64.b64encode(resp.content).decode("ascii")
        logger.debug(f"Favicon fetched successfully for {domain}")

        favicon = f"data:image/png;base64,{b64}"

        # Cache the result
        favicon_cache[domain] = favicon

        return favicon

    except Exception as e:
        logger.warning(f"[favicon] Failed to fetch or resize favicon: {e}")
        # Return a transparent 1x1 pixel as fallback
        transparent_pixel = (
            "iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGMA"
            "AQAABQABDQottAAAAABJRU5ErkJggg=="
        )
        return f"data:image/png;base64,{transparent_pixel}"


def extract_domain(raw_url: str) -> str:
    """Extract and normalize the domain from a URL.
    
    Args:
        raw_url: The URL to extract the domain from.
    
    Returns:
        The normalized domain name.
    """
    # Parse URL and get domain
    parsed_url = urlparse(raw_url)
    domain = parsed_url.netloc.lower()

    # Remove www. prefix if present
    if domain.startswith("www."):
        domain = domain[4:]
    # Special case for chatgpt.com
    if domain == "chatgpt.com":
        domain = "chatgpt.co"

    return domain